INSERT INTO public.certification VALUES ('b6bd201f-910f-4e0d-aca4-8ecba44b3c76', '37004', '2025-07-24 15:32:24.05+00', '2025-08-29 13:44:33.8542+00', 'CQP Chargé d''affaire en ingénierie énergétique', NULL, NULL, NULL, NULL, 6, '', '2025-07-24 15:32:23.798+00', '2025-07-24 15:32:23.798+00', NULL, 'UPLOADED_PDF', 'e72fac91-cb58-4930-b11f-69d24a7950ea', NULL, '2025-10-24 00:00:00+00', 'CQP Chargé d''affaire en ingénierie énergétique', 6, NULL, true, 'VALIDE_PAR_CERTIFICATEUR', '', NULL, NULL, '{}', NULL, NULL, NULL, DEFAULT, NULL, NULL, NULL, 'Le chargé d’affaire en ingénierie énergétique intervient dans le domaine du  génie énergétique (installations électriques, installation de chauffage, froid, climatisation et connexes) sur des sujets de développement d’installations ou d’amélioration de l’efficacité d’installations existantes.
INSERT INTO public.certification VALUES ('79e5b34c-415f-4ef4-a3ad-a5c78ee39f30', '36363', '2025-07-24 15:32:24.096+00', '2025-08-29 13:44:34.073113+00', 'CQP Intégrateur cabine aéronautique', NULL, NULL, NULL, NULL, 3, '', '2025-07-24 15:32:23.798+00', '2025-07-24 15:32:23.798+00', NULL, 'UPLOADED_PDF', 'e72fac91-cb58-4930-b11f-69d24a7950ea', NULL, '2025-04-25 00:00:00+00', 'CQP Intégrateur cabine aéronautique', 3, NULL, true, 'VALIDE_PAR_CERTIFICATEUR', '', NULL, NULL, '{}', NULL, NULL, NULL, DEFAULT, NULL, NULL, NULL, 'L’intégrateur cabine aéronautique assure toutes les étapes liées à l’installation d’équipements ou d’éléments au sein d’un aéronef pour le compte de constructeurs aéronautiques ou de compagnies aériennes.  ', '79e5b34c-415f-4ef4-a3ad-a5c78ee39f30');
INSERT INTO public.certification VALUES ('7eea9c96-ea66-4f9e-b83c-94e717d12ff5', '36700', '2025-07-24 15:32:24.1+00', '2025-08-29 13:44:34.116321+00', 'CQP Intégrateur câbleur aéronautique', NULL, NULL, NULL, NULL, 3, '', '2025-07-24 15:32:23.798+00', '2025-07-24 15:32:23.798+00', NULL, 'UPLOADED_PDF', 'e72fac91-cb58-4930-b11f-69d24a7950ea', NULL, '2025-07-20 00:00:00+00', 'CQP Intégrateur câbleur aéronautique', 3, NULL, true, 'VALIDE_PAR_CERTIFICATEUR', '', NULL, NULL, '{}', NULL, NULL, NULL, DEFAULT, NULL, NULL, NULL, 'L’intégrateur câbleur aéronautique assure les opérations de montage et de câblage du matériel électrique embarqué dans un sous-ensemble ou un aéronef en respectant strictement les règles liées au secteur aéronautique.
INSERT INTO public.certification VALUES ('dedc41ed-e988-4cfa-9e62-a66a30224512', '36863', '2025-07-24 15:32:24.109+00', '2025-08-29 13:44:34.1578+00', 'CQP Mécanicien en réparation navale', NULL, NULL, NULL, NULL, 3, '', '2025-07-24 15:32:23.798+00', '2025-07-24 15:32:23.798+00', NULL, 'UPLOADED_PDF', 'e72fac91-cb58-4930-b11f-69d24a7950ea', NULL, '2024-09-29 00:00:00+00', 'CQP Mécanicien en réparation navale', 3, NULL, true, 'VALIDE_PAR_CERTIFICATEUR', '', NULL, NULL, '{}', NULL, NULL, NULL, DEFAULT, NULL, NULL, NULL, 'Le mécanicien en réparation navale effectue des opérations de réparation ou d’amélioration d’ensembles mécaniques de bateaux et de navires, à l’exclusion des bateaux de plaisance. Ces navires sont d’une longueur supérieure à 20  mètres et sont équipés d’appareils propulsifs d’une puissance pouvant dépasser les 1000 chevaux : ce sont par exemple  des bateaux de croisière, des méga yachts, des ferrys, des navires de commerce (comme des cargos, des porteconteneurs, des pétroliers, des vraquiers), des brises glaces, des remorqueurs ou des navires de combat (porte-avions,  et autres bâtiments de surface…). Les ensembles mécaniques remplacés ou améliorés concernent notamment les moteurs, les appareils propulsifs, de  transmission, les compresseurs, les turbines et apparaux (pompes, vannes, réducteurs, tuyautages).  ', 'dedc41ed-e988-4cfa-9e62-a66a30224512');
INSERT INTO public.certification VALUES ('0c5d85b2-fc67-476f-8e2a-e5de636dc5d4', '36028', '2025-07-24 15:32:24.124+00', '2025-08-29 13:44:34.200385+00', 'CQP Opérateur en fabrication additive : fils polymères ou métalliques', NULL, NULL, NULL, NULL, 3, '', '2025-07-24 15:32:23.798+00', '2025-07-24 15:32:23.798+00', NULL, 'UPLOADED_PDF', 'e72fac91-cb58-4930-b11f-69d24a7950ea', NULL, '2024-11-10 00:00:00+00', 'CQP Opérateur en fabrication additive : fils polymères ou métalliques', 3, NULL, true, 'VALIDE_PAR_CERTIFICATEUR', '', NULL, NULL, '{}', NULL, NULL, NULL, DEFAULT, NULL, NULL, NULL, 'Contrairement à l’usinage qui consiste à enlever de la matière d’un bloc de matériau, la fabrication additive regroupe l’ensemble des procédés permettant de fabriquer des pièces, non plus à partir d’un bloc de matériau, mais à partir de matière première brute. Suivant les procédés, cette matière première se présente sous forme de poudre, de liquide, de pâte, de feuilles ou de filament. Elle sera transformée (liée, solidifiée, conglomérée ou fusionnée) sélectivement au cours du processus de fabrication, couche après couche, en suivant un modèle numérique pour créer la pièce tout en élaborant le matériau. On qualifiera, par comparaison, l’usinage de fabrication soustractive.
INSERT INTO public.certification VALUES ('bc0e1fe4-a295-4f7d-9e0d-2da9a362361e', '37913', '2025-07-24 15:32:24.494+00', '2025-08-29 13:44:34.233958+00', 'Bac Professionnel Carrossier peintre automobile', NULL, NULL, NULL, NULL, 4, '', '2025-07-24 15:32:23.799+00', '2025-07-24 15:32:23.799+00', NULL, 'UPLOADED_PDF', 'df503546-cc6d-4ed2-9245-2b383b42ecdb', NULL, NULL, NULL, NULL, NULL, false, 'INACTIVE', '', NULL, NULL, '{}', NULL, NULL, NULL, DEFAULT, NULL, NULL, NULL, NULL, 'bc0e1fe4-a295-4f7d-9e0d-2da9a362361e');
INSERT INTO public.certification VALUES ('611a5cab-dc2a-4a7e-9f8c-fdec06a6f747', '35885', '2025-07-24 15:32:24.059+00', '2025-08-29 13:44:33.90147+00', 'CQP Chaudronnier naval', NULL, NULL, NULL, NULL, 3, '', '2025-07-24 15:32:23.798+00', '2025-07-24 15:32:23.798+00', NULL, 'UPLOADED_PDF', 'e72fac91-cb58-4930-b11f-69d24a7950ea', NULL, '2024-09-15 00:00:00+00', 'CQP Chaudronnier naval', 3, NULL, true, 'VALIDE_PAR_CERTIFICATEUR', '', NULL, NULL, '{}', NULL, NULL, NULL, DEFAULT, NULL, NULL, NULL, 'Le chaudronnier naval réalise les travaux de construction neuve ou de réparation (suite à une avarie, déformation, corrosion...) de tout ou partie d’un ensemble ou sous-ensemble métallique d’un navire civil ou militaire (bateaux de servitude, péniches, chalutiers, paquebots, sous-marin…).  A partir de plans et/ou de gammes de préparation de sous-ensembles, il est en charge du traçage, de la découpe, de la mise en forme et de l’assemblage des pièces qui constitueront ou remplaceront les éléments principaux de la structure d’un navire (panneaux, ponts, coque, passerelle/timonerie…).', '611a5cab-dc2a-4a7e-9f8c-fdec06a6f747');
INSERT INTO public.certification VALUES ('3683e487-9f88-40f9-8617-b9b32a27d892', '35985', '2025-07-24 15:32:24.081+00', '2025-08-29 13:44:33.987257+00', 'CQP Equipier d’unité autonome de production industrielle', NULL, NULL, NULL, NULL, 3, '', '2025-07-24 15:32:23.798+00', '2025-07-24 15:32:23.798+00', NULL, 'UPLOADED_PDF', 'e72fac91-cb58-4930-b11f-69d24a7950ea', NULL, '2024-10-15 00:00:00+00', 'CQP Equipier d’unité autonome de production industrielle (CQPI)', 3, NULL, true, 'VALIDE_PAR_CERTIFICATEUR', '', NULL, NULL, '{}', NULL, NULL, NULL, DEFAULT, NULL, NULL, NULL, 'L’équipier d’unité autonome de production industrielle assure une production, en respectant strictement les règles liées à l''environnement et à la sécurité. Il est intégré au sein d’une équipe autonome qui répond à des règles de fonctionnement, il doit respecter les exigences de qualité, coûts et délai et productivité.  ', '3683e487-9f88-40f9-8617-b9b32a27d892');
INSERT INTO public.certification VALUES ('ab1fe5d5-4101-4474-999b-731fd643f133', '34239', '2025-07-24 15:32:24.093+00', '2025-08-29 13:44:34.035148+00', 'CQP Inspecteur qualité', NULL, NULL, NULL, NULL, 5, '', '2025-07-24 15:32:23.798+00', '2025-07-24 15:32:23.798+00', NULL, 'UPLOADED_PDF', 'e72fac91-cb58-4930-b11f-69d24a7950ea', NULL, '2024-10-10 00:00:00+00', 'CQP Inspecteur qualité', 5, NULL, true, 'VALIDE_PAR_CERTIFICATEUR', '', NULL, NULL, '{}', NULL, NULL, NULL, DEFAULT, NULL, NULL, NULL, 'La certification répond à tous les secteurs industriels à fort impact sécuritaire engagés dans une démarche de contrôle, de traitement des non-conformités et d’amélioration continue.   ', 'ab1fe5d5-4101-4474-999b-731fd643f133');
INSERT INTO public.certification VALUES ('08f8fdfa-fb5b-47d1-b30e-fd457a0c9d96', '36473', '2025-07-24 15:32:24.163+00', '2025-08-29 13:44:34.366578+00', 'CQP Préparateur méthodes de fabrication', NULL, NULL, NULL, NULL, 5, '', '2025-07-24 15:32:23.798+00', '2025-07-24 15:32:23.798+00', NULL, 'UPLOADED_PDF', 'e72fac91-cb58-4930-b11f-69d24a7950ea', NULL, '2027-06-01 00:00:00+00', 'CQP Préparateur méthodes de fabrication', 5, NULL, true, 'VALIDE_PAR_CERTIFICATEUR', '', NULL, NULL, '{}', NULL, NULL, NULL, DEFAULT, NULL, NULL, NULL, 'Le préparateur méthodes de fabrication détermine et formalise des procédures de fabrication et de contrôle d’un produit (usinage ou assemblage). A partir de l’analyse des données techniques, des besoins et attendus de l’entreprise (évolution ou nouveau produit, augmentation de la capacité de production, changement de moyen de production ou d’outillage, …), il rend un projet industrialisable et conforme aux cahiers des charges.', '08f8fdfa-fb5b-47d1-b30e-fd457a0c9d96');
INSERT INTO public.certification VALUES ('d5a972ee-de70-4cf0-bf77-639c54c88257', '34236', '2025-07-24 15:32:24.167+00', '2025-08-29 13:44:34.419642+00', 'CQP Préventeur santé-sécurité au travail et environnement', NULL, NULL, NULL, NULL, 5, '', '2025-07-24 15:32:23.798+00', '2025-07-24 15:32:23.798+00', NULL, 'UPLOADED_PDF', 'e72fac91-cb58-4930-b11f-69d24a7950ea', NULL, '2024-10-10 00:00:00+00', 'CQP Préventeur santé-sécurité au travail et environnement', 5, NULL, true, 'VALIDE_PAR_CERTIFICATEUR', '', NULL, NULL, '{}', NULL, NULL, NULL, DEFAULT, NULL, NULL, NULL, 'La certification répond à tous les secteurs industriels engagés dans une démarche d’amélioration des conditions de travail dans les domaines santé, sécurité du travail et environnement (SSTE).   ', 'd5a972ee-de70-4cf0-bf77-639c54c88257');
INSERT INTO public.certification VALUES ('fb0945b3-2bf0-457d-93c8-d9111404d7e9', '34172', '2025-07-24 15:32:24.18+00', '2025-08-29 13:44:34.522903+00', 'CQP Soudeur industriel', NULL, NULL, NULL, NULL, 3, '', '2025-07-24 15:32:23.799+00', '2025-07-24 15:32:23.799+00', NULL, 'UPLOADED_PDF', 'e72fac91-cb58-4930-b11f-69d24a7950ea', NULL, '2024-09-10 00:00:00+00', 'CQP Soudeur industriel', 3, NULL, true, 'VALIDE_PAR_CERTIFICATEUR', '', NULL, NULL, '{}', NULL, NULL, NULL, DEFAULT, NULL, NULL, NULL, 'Le (la) soudeur (euse) industriel (le) met en œuvre tout ou partie des procédés de soudage (TIG, MIG – MAG, électrode enrobée, oxyacétylénique, brassage...) sur différents métaux (acier, acier inoxydable, aluminium, cuivre, zirconium...) et type de pièces (formes, épaisseurs…). Le (la) soudeur (euse) industriel (le) intervient dans des entreprises de secteurs d’activités variés : charpentes métalliques, ensembles chaudronnés, tuyauteries industrielles dans les industries les plus diverses (chimie, agro-alimentaires, transports et stockage de tous les fluides et produits pulvérisant, énergies hydrauliques, thermiques, nucléaires, construction automobile, ferroviaire, navale, aéronautique, aérospatiale...).
INSERT INTO public.certification VALUES ('06ff29df-f235-4e83-87e4-eb1e93aa1ef6', '34177', '2025-07-24 15:32:24.182+00', '2025-08-29 13:44:34.59429+00', 'CQP Technicien de la qualité', NULL, NULL, NULL, NULL, 5, '', '2025-07-24 15:32:23.799+00', '2025-07-24 15:32:23.799+00', NULL, 'UPLOADED_PDF', 'e72fac91-cb58-4930-b11f-69d24a7950ea', NULL, '2024-09-10 00:00:00+00', 'CQP Technicien de la qualité (CQPI)', 5, NULL, true, 'VALIDE_PAR_CERTIFICATEUR', '', NULL, NULL, '{}', NULL, NULL, NULL, DEFAULT, NULL, NULL, NULL, 'La certification visée répond à tous les secteurs industriels engagés dans une démarche d’amélioration continue.', '06ff29df-f235-4e83-87e4-eb1e93aa1ef6');
INSERT INTO public.certification VALUES ('1504d576-82e5-449d-9597-1350fbdbfa50', '36923', '2025-07-24 15:32:24.197+00', '2025-08-29 13:44:34.631475+00', 'CQP Technicien de service maintenance en ascenseurs', NULL, NULL, NULL, NULL, 4, '', '2025-07-24 15:32:23.799+00', '2025-07-24 15:32:23.799+00', NULL, 'UPLOADED_PDF', 'e72fac91-cb58-4930-b11f-69d24a7950ea', NULL, '2025-09-29 00:00:00+00', 'CQP Technicien de service maintenance en ascenseurs', 4, NULL, true, 'VALIDE_PAR_CERTIFICATEUR', '', NULL, NULL, '{}', NULL, NULL, NULL, DEFAULT, NULL, NULL, NULL, 'Le technicien de service maintenance en ascenseurs assure des missions de maintenance préventive et corrective, principalement sur des ascenseurs, et parfois sur des escaliers mécaniques, des trottoirs roulants (horizontaux ou  sur plans inclinés dits « travelators »), des monte charges, des sièges monte escalier, des monte-plats, ou des plateformes verticales. Ses interventions s’effectuent sur des ensembles ou des sous-ensembles liés à l’EEEA  (électricité, électronique, électrotechnique, automatismes), à la mécanique, à l’hydraulique, et à l’informatique et  leurs programmations associées. Afin d’optimiser et d’améliorer la performance, la sécurité ou la configuration d’un ascenseur, le technicien de  maintenance en ascenseurs est également amené à intégrer un groupe de travail, à analyser les informations, à  participer à une action de progrès et à rechercher des solutions techniques.', '1504d576-82e5-449d-9597-1350fbdbfa50');
INSERT INTO public.certification VALUES ('b0e7a721-5d24-47b4-95d8-29b17a9e19f0', '34301', '2025-07-24 15:32:24.138+00', '2025-08-29 13:44:34.282522+00', 'CQP Opérateur en tôlerie', NULL, NULL, NULL, NULL, 3, '', '2025-07-24 15:32:23.798+00', '2025-07-24 15:32:23.798+00', NULL, 'UPLOADED_PDF', 'e72fac91-cb58-4930-b11f-69d24a7950ea', NULL, '2024-11-18 00:00:00+00', 'CQP Opérateur en tôlerie', 3, NULL, true, 'VALIDE_PAR_CERTIFICATEUR', '', NULL, NULL, '{}', NULL, NULL, NULL, DEFAULT, NULL, NULL, NULL, 'L’opérateur en tôlerie travaille exclusivement en atelier de fabrication généralement en équipe. Dans ce cadre, il peut être amené à réaliser la totalité des opérations de fabrication liées à un produit unitaire, mais également réaliser une ou plusieurs opérations de fabrication dans le cadre d’un travail en séries (dans tous les cas, il est polyvalent sur l’ensemble des activités).', 'b0e7a721-5d24-47b4-95d8-29b17a9e19f0');
INSERT INTO public.certification VALUES ('d27e8173-5837-4b9f-bbf0-8e86a343a373', '36362', '2025-07-24 15:32:24.147+00', '2025-08-29 13:44:34.32522+00', 'CQP Opérateur sur machine de frappe à froid', NULL, NULL, NULL, NULL, 3, '', '2025-07-24 15:32:23.798+00', '2025-07-24 15:32:23.798+00', NULL, 'UPLOADED_PDF', 'e72fac91-cb58-4930-b11f-69d24a7950ea', NULL, '2027-04-25 00:00:00+00', 'CQP Opérateur sur machine de frappe à froid', 3, NULL, true, 'VALIDE_PAR_CERTIFICATEUR', '', NULL, NULL, '{}', NULL, NULL, NULL, DEFAULT, NULL, NULL, NULL, 'L’opérateur sur machine de frappe à froid travaille généralement dans des ateliers de fabrication (forge, décolletage…). Il conduit une ou plusieurs machine(s) qui déforme(nt) la matière en quelques centièmes de seconde : un lopin de métal est cisaillé, puis refoulé par plusieurs frappes successives qui lui donneront sa forme finale.', 'd27e8173-5837-4b9f-bbf0-8e86a343a373');
