INSERT INTO public.certification VALUES ('3724cce0-c9e5-441e-9935-2c419c925796', '37235', '2025-07-24 15:32:24.262+00', '2025-08-29 13:44:35.023255+00', 'Mention Complémentaire (MC) post Bac - Technicien en réseaux électriques', NULL, NULL, NULL, NULL, 4, '', '2025-07-24 15:32:23.799+00', '2025-07-24 15:32:23.799+00', NULL, 'UPLOADED_PDF', 'df503546-cc6d-4ed2-9245-2b383b42ecdb', NULL, '2027-08-31 00:00:00+00', 'Technicien en réseaux électriques', 4, 'Certificat de spécialisation', true, 'VALIDE_PAR_CERTIFICATEUR', '', NULL, NULL, '{}', NULL, '2022-09-01 00:00:00+00', '2023-01-13 00:00:00+00', DEFAULT, NULL, NULL, NULL, 'Le technicien en réseaux électriques est un professionnel de la construction de réseaux électriques de distribution (réseaux aériens, souterrains et d’éclairage public). Il effectue tous les travaux de mise en œuvre et de raccordement des réseaux en application des techniques et des procédures prédéfinies.', '3724cce0-c9e5-441e-9935-2c419c925796');
