INSERT INTO public.certification VALUES ('7016b741-b611-4603-9a12-693638f13b0d', '35233', '2025-07-24 15:32:24.302+00', '2025-08-29 13:44:35.43344+00', 'Titre professionnel Assistant manager d''unité marchande - AMUM', NULL, NULL, NULL, NULL, 4, '', '2025-07-24 15:32:23.799+00', '2025-07-24 15:32:23.799+00', NULL, 'UPLOADED_PDF', '325fead1-d2c9-49c4-b02b-9a1d6f44d317', NULL, '2026-02-16 00:00:00+00', 'Assistant manager d’unité marchande', 4, 'Titre professionnel', true, 'VALIDE_PAR_CERTIFICATEUR', '', NULL, NULL, '{}', NULL, '2021-02-16 00:00:00+00', '2021-02-01 00:00:00+00', DEFAULT, NULL, NULL, NULL, 'Dans le respect de la stratégie commerciale de l''entreprise, afin de contribuer à la satisfaction et à la fidélisation du client, l''assistant manager d''unité marchande participe à l''attractivité de l''unité marchande, à la gestion des approvisionnements et au développement des ventes. Il anime l''équipe au quotidien, la mobilise pour atteindre les objectifs de vente.', '7016b741-b611-4603-9a12-693638f13b0d');
INSERT INTO public.certification VALUES ('e72abf9e-e7e4-4c97-9337-ba45078a10c4', '34858', '2025-07-24 15:32:24.307+00', '2025-08-29 13:44:35.504269+00', 'Titre professionnel Technicien en chaudronnerie - TC', NULL, NULL, NULL, NULL, 4, '', '2025-07-24 15:32:23.799+00', '2025-07-24 15:32:23.799+00', NULL, 'UPLOADED_PDF', '325fead1-d2c9-49c4-b02b-9a1d6f44d317', NULL, '2025-07-12 00:00:00+00', 'Technicien en chaudronnerie', 4, 'Titre professionnel', true, 'VALIDE_PAR_CERTIFICATEUR', '', NULL, NULL, '{}', NULL, '2020-07-12 00:00:00+00', '2020-08-24 00:00:00+00', DEFAULT, NULL, NULL, NULL, 'Le technicien en chaudronnerie réalise et conduit les opérations de transformation des métaux en feuilles, tubes et profilés.', 'e72abf9e-e7e4-4c97-9337-ba45078a10c4');
INSERT INTO public.certification VALUES ('951c6243-6079-41b0-b561-07eddf15e223', '34135', '2025-07-24 15:32:24.31+00', '2025-08-29 13:44:35.585792+00', 'Titre professionnel Chaudronnier formeur aéronautique - CFA', NULL, NULL, NULL, NULL, 3, '', '2025-07-24 15:32:23.799+00', '2025-07-24 15:32:23.799+00', NULL, 'UPLOADED_PDF', '325fead1-d2c9-49c4-b02b-9a1d6f44d317', NULL, '2024-06-24 00:00:00+00', 'Chaudronnier formeur aéronautique', 3, 'Titre professionnel', true, 'VALIDE_PAR_CERTIFICATEUR', '', NULL, NULL, '{}', NULL, '2019-06-24 00:00:00+00', '2019-07-31 00:00:00+00', DEFAULT, NULL, NULL, NULL, 'Le chaudronnier formeur aéronautique est un emploi de production contribuant à la fabrication d''un aéronef. Il conduit, de façon autonome, l''ensemble des opérations de mise en forme de pièces primaires d''un aéronef : cadres, lisses, revêtements de fuselage, longerons et nervures d''ailes, capots moteurs, etc. Au final, il assure le contrôle de la pièce et la traçabilité de ses interventions en renseignant les dossiers de fabrication.', '951c6243-6079-41b0-b561-07eddf15e223');
INSERT INTO public.certification VALUES ('b41e5179-ca2c-4d6b-a1ad-de4be14231ab', '37098', '2025-07-24 15:32:24.322+00', '2025-08-29 13:44:35.861649+00', 'Titre professionnel Conseiller de vente - CV ', NULL, NULL, NULL, NULL, 4, '', '2025-07-24 15:32:23.799+00', '2025-07-24 15:32:23.799+00', NULL, 'UPLOADED_PDF', '325fead1-d2c9-49c4-b02b-9a1d6f44d317', NULL, '2028-01-25 00:00:00+00', 'Conseiller de vente', 4, 'Titre professionnel', true, 'VALIDE_PAR_CERTIFICATEUR', '', NULL, NULL, '{}', NULL, '2023-01-25 00:00:00+00', '2022-11-25 00:00:00+00', DEFAULT, NULL, NULL, NULL, 'Le conseiller de vente exerce son activité dans un environnement commercial omnicanal. Il assure la vente de produits et de services associés nécessitant l''apport de conseils et de démonstrations auprès d''une clientèle de particuliers et parfois de professionnels. II prend en compte l''ensemble du dispositif de commercialisation : point de vente et internet. Il participe à la tenue, à l''animation du rayon et contribue aux résultats de son linéaire ou du point de vente en fonction des objectifs fixés par sa hiérarchie.', 'b41e5179-ca2c-4d6b-a1ad-de4be14231ab');
INSERT INTO public.certification VALUES ('e4e18b1b-d87b-4aa3-9cd9-5105a35154f7', '37099', '2025-07-24 15:32:24.33+00', '2025-08-29 13:44:36.08373+00', 'Titre professionnel Employé commercial - EC ', NULL, NULL, NULL, NULL, 3, '', '2025-07-24 15:32:23.799+00', '2025-07-24 15:32:23.799+00', NULL, 'UPLOADED_PDF', '325fead1-d2c9-49c4-b02b-9a1d6f44d317', NULL, '2027-12-15 00:00:00+00', 'Employé commercial', 3, 'Titre professionnel', true, 'VALIDE_PAR_CERTIFICATEUR', '', NULL, NULL, '{}', NULL, '2022-12-15 00:00:00+00', '2022-11-25 00:00:00+00', DEFAULT, NULL, NULL, NULL, 'Pour contribuer à l''attractivité de l''unité marchande et satisfaire la demande, l''employé commercial met les produits à disposition des clients. Il les accueille avec attention et répond à leur demande afin de s''assurer de leur satisfaction et renforcer leur fidélisation.', 'e4e18b1b-d87b-4aa3-9cd9-5105a35154f7');
