INSERT INTO public.certification VALUES ('c9868e66-add2-4f37-b205-524d96d3e0b4', '37676', '2025-07-24 15:32:24.522+00', '2025-08-29 13:44:42.873267+00', 'Diplôme d''Etat Educateur spécialisé - DEES', NULL, NULL, NULL, NULL, 6, '', '2025-07-24 15:32:23.799+00', '2025-07-24 15:32:23.799+00', NULL, 'UPLOADED_PDF', 'df503546-cc6d-4ed2-9245-2b383b42ecdb', NULL, '2026-08-31 00:00:00+00', 'Educateur spécialisé', 6, 'Diplôme d''Etat', true, 'VALIDE_PAR_CERTIFICATEUR', '', NULL, NULL, '{}', NULL, '2023-09-01 00:00:00+00', '2023-06-12 00:00:00+00', DEFAULT, NULL, NULL, NULL, 'L’éducateur spécialisé est un professionnel du travail social. Il exerce dans le cadre d’un mandat et de missions institutionnelles. Il accompagne, dans une démarche éducative et sociale globale, des personnes, des groupes ou des familles en difficulté dans le développement de leurs capacités de socialisation, d’autonomie, d’intégration ou d’insertion. 
