INSERT INTO public.certification VALUES ('ef51f725-ac4b-4a80-8faf-600a03504aaa', '37780', '2025-07-24 15:32:24.47+00', '2025-08-29 13:44:54.840633+00', 'Titre à finalité professionnelle Assistant de vie dépendance (ADVD)', NULL, NULL, NULL, NULL, 3, '', '2025-07-24 15:32:23.799+00', '2025-07-24 15:32:23.799+00', NULL, 'UPLOADED_PDF', '2ac78b68-5070-4f3e-bc7c-5884e28810d0', '2030-07-19 00:00:00+00', '2026-07-19 00:00:00+00', 'Assistant de vie dépendance', 3, NULL, true, 'VALIDE_PAR_CERTIFICATEUR', '', NULL, NULL, '{}', NULL, NULL, NULL, DEFAULT, NULL, NULL, NULL, 'L''assistant de vie dépendance accompagne les particuliers employeurs en perte d’autonomie ou en situation de handicap dans leurs vies quotidiennes afin de contribuer à leur maintien à domicile.
INSERT INTO public.certification VALUES ('333de868-2673-43b7-812c-da0667076853', '37675', '2025-07-24 15:32:24.525+00', '2025-08-29 13:44:55.533307+00', 'Diplôme d''Etat Assistant de service social - DEASS', NULL, NULL, NULL, NULL, 6, '', '2025-07-24 15:32:23.799+00', '2025-07-24 15:32:23.799+00', NULL, 'UPLOADED_PDF', '1132b130-242e-42c0-8740-010d1d1f4e57', NULL, '2026-08-31 00:00:00+00', 'Assistant de service social', 6, 'Diplôme d''Etat', true, 'VALIDE_PAR_CERTIFICATEUR', '', NULL, NULL, '{}', NULL, '2023-09-01 00:00:00+00', '2023-06-12 00:00:00+00', DEFAULT, NULL, NULL, NULL, 'L’assistant de service social est un professionnel du travail social. Il exerce dans le cadre d’un mandat et de missions institutionnelles. Il mène des interventions sociales, individuelles ou collectives, en vue d’améliorer par une approche globale et d’accompagnement social les conditions de vie des personnes et des familles. 
