INSERT INTO public.certification_competence_bloc VALUES ('656523f5-cc5e-498f-85ae-b7647dd721aa', '2025-08-29 13:40:38.071+00', 'RNCP35513BC09', 'Action en responsabilité au sein d’une organisation professionnelle', 'ca4601d3-0bc8-412b-a774-3ea8372d2d54', '     ● Situer   son rôle et sa mission au sein d’une organisation pour s’adapter et prendre   des initiatives ● Respecter   les principes d’éthique, de déontologie et de responsabilité environnementale ● Travailler   en équipe et en réseau ainsi qu’en autonomie et responsabilité au service   d’un projet ● Analyser   ses actions en situation professionnelle, s’autoévaluer pour améliorer sa pratique     ');
INSERT INTO public.certification_competence_bloc VALUES ('bf5b9886-2872-45b1-b719-e5ad54a0e7d7', '2025-08-29 13:40:38.078+00', 'RNCP35513BC10', 'Positionnement vis à vis d’un champ professionnel', 'ca4601d3-0bc8-412b-a774-3ea8372d2d54', '  ●  Identifier et situer les champs professionnels potentiellement en relation avec les acquis et la mention ainsi que les parcours possibles pour y accéder ● Caractériser et valoriser son identité, ses compétences et son projet professionnel en fonction d’un contexte Identifier le processus de production, de diffusion et de valorisation des savoirs ');
INSERT INTO public.certification_competence_bloc VALUES ('c1bd19c8-d8be-4308-9c10-90b231de4843', '2025-08-29 13:40:38.03+00', 'RNCP35513BC01', 'Concevoir des interventions adaptées aux enjeux de la société', 'ca4601d3-0bc8-412b-a774-3ea8372d2d54', '     - Repérer les sources   d''analyse et de diagnostic, être en capacité d''y accéder  - Appréhender les   grands enjeux culturels, historiques et les fondamentaux sociétaux  - Relever les éléments   de contextes (sociaux, économiques, culturels, démographiques, politiques,   territoriaux, éducatifs, …) et interroger les acteurs concernés  - Mettre en place des   outils d''enquêtes adaptés aux publics et aux contextes et présenter les   résultats - Analyser les   attentes, les besoins des populations et des publics, et en comprendre les   enjeux - Repérer les   dynamiques propres à des publics spécifiques - Sélectionner,   référencer les sources et justifier ses choix méthodologiques - Mobiliser les outils   pertinents pour analyser, représenter des données multiples et discuter les   résultat - Présenter, partager   des résultats et formuler des ébauches de préconisations - Proposer des réponses   et des actions cohérentes avec les analyses réalisées - Mettre au débat ses   analyses et les propositions qui en découlent - Concevoir des outils   de veille et de prospective avec les parties prenantes - Impliquer les   différents acteurs de la société dans les propositions de remédiations/action       ');
INSERT INTO public.certification_competence_bloc VALUES ('6cac085a-df0c-4658-a5ae-33d7b07ea367', '2025-08-29 13:40:38.036+00', 'RNCP35513BC02', 'Construire des dynamiques partenariales', 'ca4601d3-0bc8-412b-a774-3ea8372d2d54', '     - Repérer les acteurs   socio-économiques- - Adopter une posture   et une communication adaptée aux contextes et aux acteurs - Mobiliser des   partenaires et diversifier ses contacts (étendue géographique, inclusion de   tous les publics, diversités des domaines de compétences …) - Identifier les   politiques sociales et les dispositifs d’action sociale nécessaires à la mise   en œuvre d’un projet partenarial  - Distinguer les   différentes catégories de partenariat - Reconnaître et   intégrer les enjeux et les logiques à l’œuvre dans les dynamiques   partenariales - Construire un   argumentaire et faire des propositions - S''intégrer à un   réseau d’acteurs adapté au contexte - Réaliser une veille   au sein de ses réseaux pour identifier de nouvelles attentes et nouveaux   besoins sociaux (expérimentation)  - Analyser les enjeux   de la dynamique partenariale  - Évaluer l’adéquation   entre le partenariat existant et les projets  - Développer le réseau   en mobilisant les ressources pour collaborer à un projet ou une action   collective  - Créer les conditions   pour entretenir la dynamique partenariale     ');
INSERT INTO public.certification_competence_bloc VALUES ('806b9358-0feb-42c6-8f2c-4682942efa59', '2025-08-29 13:40:38.042+00', 'RNCP35513BC03', 'Conduire un projet pour des établissements et services sanitaires et sociaux', 'ca4601d3-0bc8-412b-a774-3ea8372d2d54', '       - Comprendre une   commande et se l''approprier - Identifier le réseau des acteurs pouvant   intervenir dans le projet ainsi que leur(s) rôle(s) - Recueillir des   éléments de constat spécifiques aux établissements-services   sanitaires-sociaux (SAAD, SIAD, EHPAD...) - Formaliser un   diagnostic dans le cadre d''un projet - Définir, mettre en   oeuvre et coordonner une démarche méthodologique  - Mobiliser des outils   de la démarche qualité et/ou de conduite de projet - Apporter et défendre   des solutions-actions adaptées par rapport au diagnostic dans un contexte   spécifique - Définir des   indicateurs de réussite ou d''avancement des actions - Identifier des outils   de veille pour repérer les appels à projets - Formaliser un budget et suivre   celui-ci dans le respect des contraintes réglementaires  - Rendre le dossier   d''appel à projets dans les conditions imparties  - Présenter la réponse   à l''appel à projets au(x) commanditaire(s)     ');
INSERT INTO public.certification_competence_bloc VALUES ('a4e0b5e8-c8d9-4879-89fd-2cf0e6b26068', '2025-08-29 13:40:38.047+00', 'RNCP35513BC04', 'Construire des accompagnements adaptés', 'ca4601d3-0bc8-412b-a774-3ea8372d2d54', '     - Analyser les   attentes, les besoins des populations et des publics spécifiques aux   structures sanitaires et sociales (SAAD, SIAD, EHPAD...) - Mobiliser une posture   et une communication professionnelles adaptées aux publics rencontrés et à   leur situation personnelle - Accueillir les   différents publics dans le respect des recommandations professionnelles et   déontologiques   - Identifier les   principaux acteurs institutionnels et politiques publiques à différents   niveaux territoriaux - Proposer les aides et   accompagnements mobilisables aux publics en recourant à une évaluation des   besoins - Accompagner les   publics dans leurs démarches administratives ou de demandes d''aides - Identifier des   solutions technologiques et/ou numériques mobilisables et les règles   éthiques-déontologiques à respecter - Adapter les   aides-accompagnements-prises en charge en prenant en compte leur   environnement - Respecter le cadre   légal et réglementaire, budgétaire - Mobiliser des   outils-démarches innovants d''adaptation de l''environnement des publics - Participer à la mise   en place de parcours de santé - Informer les publics   et/ou les structures sur les conditions de mise en œuvre et les contraintes   des solutions technologiques et/ou numériques existantes     ');
INSERT INTO public.certification_competence_bloc VALUES ('090e2c78-e37f-4bbf-a33d-de7bbffdfd1d', '2025-08-29 13:40:38.053+00', 'RNCP35513BC05', 'Assurer l’encadrement et la coordination', 'ca4601d3-0bc8-412b-a774-3ea8372d2d54', '     - Communiquer de façon professionnelle dans   une équipe en s''adaptant à son public (oralement et par écrit) - Mobiliser les outils professionnels de   travaux bureautiques et/ou collaboratifs - Se positionner dans un collectif   de travail et une organisation - Utiliser des techniques d''animation et de   gestion d''équipe - Participer à des dispositifs d''échanges   avec les collaborateurs-partenaires sociaux - Prévenir et gérer des conflits - Mettre en place des pratiques RH adaptées   aux besoins des secteurs sanitaires et sociaux  - Renseigner les tableaux de bord de gestion   existants - Planifier les activités et les plannings   des professionnels - Décliner les politiques RH de   l''établissement, du service ou du groupe - Entretenir et favoriser la motivation de   l''équipe - Ajuster les actions à partir des   indicateurs issus des tableaux de bord de gestion existants  - Contribuer à la mise en place de démarches   de prévention et d''amélioration - Intégrer des principes et démarches   d''organisation innovants - Améliorer les pratiques de planification   et faire face aux imprévus     ');
